import React, { useState } from 'react';
import PropTypes from 'prop-types';
import styles from '../Profile.module.css';
import mutationUpdateProfile from '../../LeaderBoard/requests/mutationUpdateFavoriteProfile';

const LeftPanel = ({ profile, personId, setVisibleForm }) => {
  const [favorite, setFavorite] = useState(profile.favorite);
  console.log(profile);
  function updateFavorite(PersonId, isFavorite) {
    mutationUpdateProfile(localStorage.accessToken, localStorage.client, localStorage.uid, PersonId, isFavorite)
      .catch(error => {
        console.log(error);
      })
      .then(() => {
        setFavorite(isFavorite);
      });
  }

  if (profile === '') {
    return <p>Loading LeftPanelâ€¦</p>;
  }
  return (
    <>
      <div className={styles.sideBar}>
        <div>
          <div className={styles.userInfo}>
            {personId === undefined && (
              <button onClick={() => setVisibleForm(true)} className={styles.editProfile}>
                <img
                  src="data:image/svg+xml;base64,"
                  className={styles.editSvg}
                />
              </button>
            )}
            {personId && (
              <div className={styles.editProfile}>
                {!favorite && (
                  <img
                    onClick={() => updateFavorite(profile.batter_datraks_id, true)}
                    src="data:image/svg+xml;base64,"
                    className={styles.like}
                  />
                )}
                {favorite && (
                  <img
                    src="data:image/svg+xml;base64,"
                    onClick={() => updateFavorite(profile.batter_datraks_id, false)}
                    className={styles.like}
                  />
                )}
              </div>
            )}

            <div className={styles.wrapperAvatar}>
              <div className={styles.avatar}></div>
            </div>
            <div className={styles.wrapperName}>
              <div className={styles.name}>
                {profile.first_name} {profile.last_name}
              </div>
              <div className={styles.position}>{profile.position}</div>
              <div className={styles.position2}>{profile.position2}</div>
            </div>
          </div>
        </div>
        <div className={styles.personalInfo}>
          <div className={styles.item}>
            <span className={styles.wrapperSvg}>
              <img
                src="data:image/svg+xml;base64,"
                className={styles.ageSvg}
              />
            </span>
            <span>age</span>
            <span> {profile.age} </span>
          </div>
          <div className={styles.item}>
            <span>
              <img
                src="data:image/svg+xml;base64,"
                className={styles.heightSvg}
              />
            </span>
            <span>Height</span>
            <span>
              ft {profile.feet} in {profile.inches}
            </span>
          </div>
          <div className={styles.item}>
            <span>
              <img
                src="data:image/svg+xml;base64,"
                className={styles.weightSvg}
              />
            </span>
            <span>Weight</span>
            <span>{profile.weight}</span>
          </div>
          <div className={styles.item}>
            <span>
              <img
                src="data:image/svg+xml;base64,"
                className={styles.ballSvg}
              />
            </span>
            <span>Throws </span>
            <span>{profile.throws_hand}</span>
          </div>
          <div className={styles.item}>
            <span>
              <img
                src="data:image/svg+xml;base64,"
                className={styles.batSvg}
              />
            </span>
            <span>Bats </span>
            <span>{profile.bats_hand}</span>
          </div>
        </div>
        <div className={styles.schoolInfo}>
          {profile.school != null && (
            <div className={styles.schoolInfoItemWrapper}>
              <div className={styles.schoolInfoItemLabel}>School</div>
              <div className={styles.schoolInfoItem}>{profile.school.name}</div>
            </div>
          )}

          {profile.school_year != null && (
            <div className={styles.schoolInfoItemWrapper}>
              <div className={styles.schoolInfoItemLabel}>School Year</div>
              <div className={styles.schoolInfoItem}>{profile.school_year}</div>
            </div>
          )}
          {profile.teams !== '' && (
            <div className={styles.schoolInfoItemWrapper}>
              <div className={styles.schoolInfoItemLabel}>Team</div>
              <div className={styles.schoolInfoItem}>{profile.teams.map(team => `${team.name}, `)}</div>
            </div>
          )}
          {!profile.facilities.length !== true && (
            <div className={styles.schoolInfoItemWrapper}>
              <div className={styles.schoolInfoItemLabel}>Facility</div>
              <div className={styles.schoolInfoItem}>{profile.facilities[0].u_name}</div>
            </div>
          )}
          {profile.biography !== '' && (
            <div className={styles.schoolInfoItemWrapper}>
              <div className={styles.schoolInfoItemLabel}>About</div>
              <div className={styles.schoolInfoItem}>{profile.biography}</div>
            </div>
          )}
        </div>
      </div>
      {/* )} */}
    </>
  );
};

LeftPanel.propTypes = {
  profile: PropTypes.object,
  setVisibleForm: PropTypes.func,
  personId: PropTypes.object,
};

export default LeftPanel;
